func (c *current) onClassSlot1(name, args, block any) (any, error) {
	blk := block.(Block)
	blk.Named = name.(string)
	blk.Form = Seq{blk.Form, Self{}}
	return SlotDecl{
		Named: name.(string),
		Value: FunDecl{
			Named: name.(string),
			Args:  args.([]SlotDecl),
			Ret:   NewRecordType(name.(string)),
			Form:  blk,
		},
		Visibility: PrivateVisibility,
	}, nil
}

